'use client'

import type { StaticImageData } from 'next/image'
import NextImage from 'next/image'
import React from 'react'
import { cn } from '@/utilities/ui'

import type { Props as MediaProps } from '../types'
import { cssVariables } from '@/cssVariables'
import { getMediaUrl } from '@/utilities/getMediaUrl'

const { breakpoints } = cssVariables

// Base64 placeholder while the image loads
const placeholderBlur =
  'data:image/png;base64,'

// Helpers to safely normalize values (avoid calling .trim on non-strings)
const asNonEmptyString = (v: unknown): string | null => {
  if (typeof v === 'string') {
    const t = v.trim()
    return t.length ? t : null
  }
  return null
} // Using guards prevents “property 'trim' on type 'never'” by narrowing unions first. [web:156][web:165]

type MaybeSrc = StaticImageData | string | null | undefined

export const ImageMedia: React.FC<MediaProps> = (props) => {
  const {
    alt: altFromProps,
    fill,
    pictureClassName,
    imgClassName,
    priority,
    resource,
    size: sizeFromProps,
    src: srcFromProps,
    loading: loadingFromProps,
  } = props

  let width: number | undefined
  let height: number | undefined
  let alt = altFromProps || ''

  // Resolve src from prop or resource, but never default to '' (empty string)
  let resolved: MaybeSrc = srcFromProps ?? null

  // If resource exists, build URL and coerce cache tag to string for the util
  if (!resolved && resource && typeof resource === 'object') {
    const {
      alt: altFromResource,
      height: h,
      url,
      width: w,
      updatedAt,
    } = resource as {
      alt?: string
      height?: number
      width?: number
      url?: string | null
      updatedAt?: string | number | null
    }

    width = w
    height = h
    alt = alt || altFromResource || ''

    // Normalize cache tag to a string | null | undefined to satisfy the util signature
    const cacheTag: string | null | undefined =
      updatedAt == null ? undefined : String(updatedAt) // Avoids passing number to a string parameter. [web:156]

    const withCache = getMediaUrl(url ?? undefined, cacheTag)

    // Prefer a non-empty string; otherwise keep null
    resolved = (asNonEmptyString(withCache) ?? null) as string | null
  }

  // If the src prop was a string, ensure it isn’t empty; StaticImageData is allowed as-is
  if (typeof resolved === 'string') {
    resolved = asNonEmptyString(resolved)
  }

  const loading = loadingFromProps || (!priority ? 'lazy' : undefined)

  const sizes =
    sizeFromProps ||
    Object.entries(breakpoints)
      .map(([, value]) => `(max-width: ${value}px) ${value * 2}w`)
      .join(', ')

  // Do not render when no valid src is available (prevents empty-src network fetch). [web:131][web:124][web:135]
  if (!resolved) return null

  return (
    <picture className={cn(pictureClassName)}>
      <NextImage
        alt={alt}
        className={cn(imgClassName)}
        fill={fill}
        height={!fill ? height : undefined}
        placeholder="blur"
        blurDataURL={placeholderBlur}
        priority={priority}
        quality={100}
        loading={loading}
        sizes={sizes}
        src={resolved}
        width={!fill ? width : undefined}
      />
    </picture>
  )
}
